import {Link} from "react-router-dom";
import MakeCurrentlyReading from "./MakeCurrentlyReading";
import MakeRead from "./MakeRead";
import MakeWannaRead from "./MakeWannaRead";

const BookShelf = ({Shelf, handleMoveToShelf}) => {

  const handleMoving = (e, book) =>{
    const Book = book;
    const shelf = e.target.value;
    const id =book.id;
    console.log(Book.title, shelf, id)
    handleMoveToShelf(Book, shelf, id)
};

console.log(Shelf)

  return (
    <div className='app'>
    <div className="list-books">

      <div className="list-books-title">
        <h1>MyReads</h1>
      </div>

      <div className="list-books-content">

        {/*Shelfes */}
        <div>

          {/* Currently Reading Shelf */}
          <div className="bookshelf">
            <h2 className="bookshelf-title">Currently Reading</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                {/* {Shelf.map((obj) => {
                    if(obj['name'] === 'currentlyReading'){
                      const book = obj['data'][0]
                      return(<MakeCurrentlyReading handleMoving={handleMoving} book={book} />)
                    }
                  })} */}
              </ol>
            </div>
          </div>

          {/* Want to Read Shelf */}
          <div className="bookshelf">
            <h2 className="bookshelf-title">Want to Read</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                {/* {Shelf.map((obj) => {
                    if(obj['name'] === 'wantToRead'){
                      const book = obj['data'][0];
                      return (<MakeWannaRead handleMoving={handleMoving} book={book} />
                      )}
                  })} */}
              </ol>
            </div>
          </div>

          {/* Read Shelf */}
          <div className="bookshelf">
            <h2 className="bookshelf-title">Read</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                {/* {Shelf.map((obj) => {
                    if(obj['name'] === 'read'){
                      const book = obj['data'][0]
                      if(book){
                        return(<MakeRead handleMoving={handleMoving}  book={book} />)
                      }
                    }
                  })} */}
              </ol>
            </div>
          </div>

        </div>

      </div>

    </div>
    <div className='open-search'>
      <Link to="/Search" >Add a book </Link>
    </div>
    </div>
  );
};

export default BookShelf;
