/* eslint-disable react-hooks/exhaustive-deps */
import { useEffect, useState } from "react";

const HandleResult = ({result,  handleMoveToShelf, Shelf}) =>{
    const [returnvalue, setReturnvalue] =useState('Type in search box');
    const [booksInShelfs, setBooksInShelfs] = useState(false)
    const [shelf, setShelf] = useState('')
    
    const handleInShelf = (shelf) =>{
        setShelf(shelf);
        setBooksInShelfs(true);
        result.filter(obj => obj.id ===shelf.id)
        console.log(shelf.title);
    }


useEffect(() => {
    result.filter(obj => obj.id !==shelf.id)
}, [shelf,result,booksInShelfs])


    const handleMoving = (e) =>{
        const book = result.filter(obj => obj.id === e.target.id)
        const shelf = e.target.value;
        handleMoveToShelf(book, shelf)
    };
    
    useEffect(() =>{
        if(result === [] || result === null || result === 'Type in search box'){
            setReturnvalue('Type in search box');
        }
        if(result ==="No results"){
            setReturnvalue("No results");
        }
        if(result.length !== 0 && typeof result == 'object'){
            (result.filter(obj => obj.id !==shelf.id)).th
            setReturnvalue(
                    <ol className="books-grid">
                        {result.map(obj =>{
                            return(
                                <li key={obj.id}>
                                    <div className="book">
                                        <div className="book-top">
                                            {obj.imageLinks === undefined ?
                                                <div
                                                className="book-cover"
                                                style={{
                                                width: 128,
                                                height: 193
                                                }}
                                                >No Iamge</div>
                                            :
                                                <div
                                                className="book-cover"
                                                style={{
                                                width: 128,
                                                height: 193,
                                                backgroundImage:
                                                `url(${obj.imageLinks.thumbnail})`
                                                }}
                                                ></div>
                                            }
                                            <div className="book-shelf-changer">
                                                {Shelf.map(book => {
                                                    if(book.id === obj.id){
                                                        handleInShelf(book)
                                                    }
                                                })}

                                                {
                                                    booksInShelfs ?
                                                        <select onChange={handleMoving} id={obj.id} defaultValue={shelf}>
                                                            <option value="none" className="disabled-option">Move to... </option>
                                                            <option value="currentlyReading">Currently Reading</option>
                                                            <option value="wantToRead">Want to Read</option>
                                                            <option value="read">Read</option>
                                                            <option value="none">None</option>
                                                        </select>
                                                    :
                                                        <select onChange={handleMoving} id={obj.id}>
                                                            <option value="none" className="disabled-option">Move to... </option>
                                                            <option value="currentlyReading">Currently Reading</option>
                                                            <option value="wantToRead">Want to Read</option>
                                                            <option value="read">Read</option>
                                                        </select>
                                                }
                                                {/* {console.log(booksInShelfs ? booksInShelfs: null,obj.title, obj.id)} */}
                                            </div>
                                        </div>

                                        <div className="book-title">{obj.title}</div>
                                        <div className="book-authors">{obj.authors}</div>
                                    </div>
                                </li>
                                
                                )})}
                    </ol>
                );
        }
    },[result, booksInShelfs, Shelf, shelf])

    return (returnvalue);
}

export default HandleResult;
