/* eslint-disable eqeqeq */
/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable no-unused-vars */

import { useEffect, useRef, useState } from "react";
import {Link} from "react-router-dom";
import Output from './SearchOutput'
import HandleResult from './HandleResult'

const SearchPage = ({updateValue}, {setUpdateValue}) => {
    const  [result, setResult] = useState([]);
    const inputElement = useRef();
    
    useEffect(()=>{
        inputElement.current.focus();
    },[])
    
    const handleSearch = (e) =>{
        if(e.target.value ===' ' || e.target.value[e.target.value.length-1] ===' '|| e.target.value.length === 0){
            setResult("typeing");
        }else{
            Output(setResult, e.target.value, 'search')
            setResult("wait for results");
        }
    }
    
    
    

    return(
        <div className="search-books">
            <div className="search-books-bar">
                <Link to="/" className="close-search">Close</Link>
                <div className="search-books-input-wrapper">
                    <input
                    type="text"
                    placeholder="Search by title"
                    onChange={handleSearch}
                    ref={inputElement}
                    />
                </div>
            </div>

            <div className="search-books-results">
                <div className="books-grid">
                    <HandleResult result={result}setUpdateValue={setUpdateValue}  />
                </div>
            </div>
        </div>
        )
}

/*

        */


export default SearchPage;
