import "./App.css";
import {Route, Routes} from 'react-router-dom'
import BookShelf from "./Components/BookShelf";
import SearchPage from "./Components/SearchPage";
import { useEffect, useState } from "react";

function App() {
  const [Shelf, setShelf] = useState([]);

  const[currentReadShelf, setCurrentReadShelf] = useState([]);
  const[wannaToReadShelf, setWannaToReadShelf] = useState([]);
  const[readingShelf, setReadingShelf] = useState([]);



  const handleMoveToShelf = (book, shelf) =>{
    if(shelf ==='currentlyReading'){
      setCurrentReadShelf(book);
      setShelf([...Shelf], 
          {name:shelf, data:book}
        )
    }
    
    else if(shelf ==='wantToRead')
    {
      setWannaToReadShelf(book);
      setShelf([...Shelf], 
        {name:shelf, data:book}
      )
    }
    
    else if(shelf ==='read')
    {
      setReadingShelf(book);
      setShelf([...Shelf], 
        {name:shelf, data:book}
      )
    }
  };


console.log(Shelf)
return (
    <Routes>
      <Route exact path="/" element={<BookShelf  />}/>
      <Route exact path="/Search" element={
      <SearchPage handleMoveToShelf={handleMoveToShelf} />}/>  
    </Routes>
  );
}

export default App;
