/* eslint-disable eqeqeq */
/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable no-unused-vars */

import { useEffect, useRef, useState } from "react";
import {Link} from "react-router-dom";
import Output from './SearchOutput'

const SearchPage = () => {
    const  [result, setResult] = useState([]);
    const inputElement = useRef();
    
    useEffect(()=>{
        inputElement.current.focus();
    },[])
    
    const handleSearch = (e) =>{
        if(e.target.value ===' ' || e.target.value[e.target.value.length-1] ===' '|| e.target.value.length === 0){
            setResult("");
            /* console.log('%c result clearedðŸ§¹ Postion One', 
                'background-color:black; color:white;font-size:30px;') */
        }else{
            Output(setResult, e.target.value)
            setResult("wait for results");
            /* console.log('%c result sent ðŸ“© Postion Two',
                'background-color:black; color:white;font-size:30px;') */
        }
        console.log(result, result.length)
    }
    
    
    const handleResult = () =>{
        switch(result){
            case result.length===0:
                return (<h1>Type in search box</h1>)

            case result ==="wait for results":
                return (<h1>Searching...</h1>)

            case typeof result === 'object':
                return (
                    <ol className="books-grid">
                        {result.map(obj =>{
                            return(
                                <li key={obj.id}>
                                    <div className="book">
                                        <div className="book-top">
                                            <div
                                                className="book-cover"
                                                style={{
                                                width: 128,
                                                height: 193,
                                                backgroundImage:
                                                    `url(${obj.imageLinks.thumbnail})`
                                                }}
                                            ></div>
                    
                                            <div className="book-shelf-changer">
                                                <select>
                                                    <option value="none" disabled>
                                                        Move to...
                                                    </option>
                                                    <option value="currentlyReading">
                                                        Currently Reading
                                                    </option>
                                                    <option value="wantToRead">Want to Read</option>
                                                    <option value="read">Read</option>
                                                    <option value="none">None</option>
                                                </select>
                                            </div>
                                        </div>
                                        <div className="book-title">{obj.title}</div>
                                        <div className="book-authors">{obj.authors[0]}</div>
                                    </div>
                                </li>)})}
                    </ol>
                );
            default:
        }
    }

    return(
        <div className="search-books">
            <div className="search-books-bar">
                <Link to="/" className="close-search">Close</Link>
                <div className="search-books-input-wrapper">
                    <input
                    type="text"
                    placeholder="Search by title"
                    onChange={handleSearch}
                    ref={inputElement}
                    />
                </div>
            </div>

            <div className="search-books-results">
                
            </div>
        </div>
        )
}

/*

        */


export default SearchPage;
