import "./App.css";
import {Route, Routes} from 'react-router-dom'
import BookShelf from "./Components/BookShelf";
import SearchPage from "./Components/SearchPage";
import { useState } from "react";

function App() {
  const [updateValue, setUpdateValue] = useState([]);

  const[currentReadShelf, setCurrentReadShelf] = useState(['']);
  const[wannaToReadShelf, setWannaToReadShelf] = useState(['']);
  const[readingShelf, setReadingShelf] = useState(['']);

console.log(currentReadShelf,wannaToReadShelf,readingShelf)

  const handleMoveToShelf = (book, shelf) =>{
    if(shelf ==='currentlyReading'){
      setCurrentReadShelf(...currentReadShelf);
    }
    
    if(shelf ==='wantToRead')
    {
      setWannaToReadShelf(...wannaToReadShelf, book);
    }
    
    if(shelf ==='read')
    {
      setReadingShelf(...readingShelf, book);
    }
    
  };


return (
    <Routes>
      <Route exact path="/" element={<BookShelf updateValue={updateValue} />}/>
      <Route exact path="/Search" element={
      <SearchPage updateValue={updateValue} setUpdateValue={setUpdateValue} handleMoveToShelf={handleMoveToShelf} />}/>  
    </Routes>
  );
}

export default App;
