import "./App.css";
import {Route, Routes} from 'react-router-dom'
import BookShelf from "./Components/BookShelf";
import SearchPage from "./Components/SearchPage";
import {useState } from "react";

function App() {
  const [Shelf, setShelf] = useState([]);

  const handleMoveToShelf = (book, shelf, id) =>{
    
    if(shelf === 'none'){
      setShelf((prev) =>(
        prev.filter(obj => obj.id !== id)))
    }
    else if(Shelf.length === 0 && shelf !== 'none'){
      
      setShelf((prev) =>(
        [...prev, 
          {name:shelf, data:book, id:id}]))
        Shelf.length = Shelf.length +1
        console.log('add from first', Shelf.length)
        
    }
    else{
      Shelf
      .map(
        obj =>{
          if(obj.id !== id ){
            setShelf((prev) =>(
              [...prev, 
                {name:shelf, data:book, id:id}]))
                console.log('add from sceond')
          }
        }
      )
    }
    window.location.href = 'http://localhost:3000/';
  };

console.log(Shelf.length)

return (
    <Routes>
      <Route exact path="/" element={<BookShelf  Shelf={Shelf} handleMoveToShelf={handleMoveToShelf} />}/>
      <Route exact path="/Search" element={
      <SearchPage handleMoveToShelf={handleMoveToShelf} />}/>  
    </Routes>
  );
}

export default App;
