/* eslint-disable eqeqeq */
/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable no-unused-vars */

import { useState } from "react";
import {Link} from "react-router-dom";
import Output from './SearchOutput'

const SearchPage = () => {
    const  [result, setResult] = useState([]);
    const  [ShowSearchReult, setShowSearchReult] =useState(true);
    
    const handleSearch = (e) =>{
        if(e.target.value ==='' || e.target.value ===' ' ){
            setResult(false);
            setShowSearchReult(!ShowSearchReult);
        }
        Output(setResult,e.target.value)
        console.log(result)
    }
    
    
    return(
        <div className="search-books">
            <div className="search-books-bar">
            <Link to="/" className="close-search">Close</Link>
            <div className="search-books-input-wrapper">
                <input
                type="text"
                placeholder="Search by title"
                onChange={handleSearch}
                />
            </div>
            </div>
            <div className="search-books-results">
                <ol className="books-grid">
                    {ShowSearchReult ? 
                    
                    result==="No results" ?
                        <h1>No Results</h1>
                    :
                        result.map(obj =>{
                            return(
                                <li key={obj.id}>
                                    <div className="book">
                                        <div className="book-top">
                                            <div
                                                className="book-cover"
                                                style={{
                                                width: 128,
                                                height: 193,
                                                backgroundImage:
                                                    `url(${obj.imageLinks.thumbnail})`,
                                                }}
                                            ></div>

                                            <div className="book-shelf-changer">
                                                <select>
                                                    <option value="none" disabled>
                                                        Move to...
                                                    </option>
                                                    <option value="currentlyReading">
                                                        Currently Reading
                                                    </option>
                                                    <option value="wantToRead">Want to Read</option>
                                                    <option value="read">Read</option>
                                                    <option value="none">None</option>
                                                </select>
                                            </div>
                                        </div>
                                        <div className="book-title">{obj.title}</div>
                                        <div className="book-authors">{obj.authors[0]}</div>
                                    </div>
                                </li>)})
                    :
                    <h1>Type in search box</h1>
                    }
                </ol>
            </div>
        </div>
        )
}

export default SearchPage;
