import "./App.css";
import {Route, Routes} from 'react-router-dom'
import MainPage from "./Components/MainPage";
import SearchPage from "./Components/SearchPage";
import {useEffect, useState } from "react";
import Output from "./Components/Output";

function App() {
  const [Shelf, setShelf] = useState(['']);
  const [updatestate, setUpdateState] = useState(true);


  const handleMoveToShelf = (book, shelf) =>{

    if(book.shelf){
      setShelf((prev) =>(prev.filter(obj => obj.id !== book.id)));
        Output('update', book, shelf);
    }

    if(shelf === 'none' && !(book.shelf)){
      setShelf((prev) =>(prev.filter(obj => obj.id !== book.id)));
      Output('update', book[0],shelf);
    }

    if(shelf !== 'none' && !(book.shelf)){
      Output('update', book[0],shelf);
    }

    /* setInterval(() => {
      window.re
    }, 400);  */
    console.log('add', book[0].title)
    setUpdateState(!updatestate)
  };

  useEffect(() => {
    Output('getall',setShelf)
  }, [updatestate])

return (
    <Routes>
      <Route exact path="/" element={<MainPage  
      Shelf={Shelf} handleMoveToShelf={handleMoveToShelf}
      />}/>

      <Route exact path="/Search" element={
      <SearchPage handleMoveToShelf={handleMoveToShelf} Shelf={Shelf} updatestate={updatestate}/>}/>  
    </Routes>
  );
}

export default App;
