import { useEffect, useState } from "react";



const HandleResult = ({result}) =>{
    const [returnvalue, setReturnvalue] =useState(('Type in search box'));
    let retun = (returnvalue);
    useEffect(() =>{
        if(result.length===0 && result !== ''){
            setReturnvalue('Type in search box');
        }

        if(result ==="wait for results"){
            setReturnvalue('Searching...');
        }

        if(typeof result === 'object'){
            setReturnvalue(
                    <ol className="books-grid">
                        {result.map(obj =>{
                            return(
                                <li key={obj.id}>
                                    <div className="book">
                                        <div className="book-top">
                                            <div
                                                className="book-cover"
                                                style={{
                                                width: 128,
                                                height: 193,
                                                backgroundImage:
                                                    `url(${obj.imageLinks.thumbnail})`
                                                }}
                                            ></div>
                                            <div className="book-shelf-changer">
                                                <select>
                                                    <option value="none" disabled>
                                                        Move to...
                                                    </option>
                                                    <option value="currentlyReading">
                                                        Currently Reading
                                                    </option>
                                                    <option value="wantToRead">Want to Read</option>
                                                    <option value="read">Read</option>
                                                    <option value="none">None</option>
                                                </select>
                                            </div>
                                        </div>
                                        <div className="book-title">{obj.title}</div>
                                        <div className="book-authors">{obj.authors[0]}</div>
                                    </div>
                                </li>)})}
                    </ol>
                );
                // eslint-disable-next-line react-hooks/exhaustive-deps
                retun = returnvalue;
        }
    },[result])
    return (retun);
}

export default HandleResult;
