/* eslint-disable no-unused-vars */
import * as BookAPI from '../BooksAPI';

const Output = (setResult, searchWord, Method) =>{
    
    if(Method ==='search'){
        const SearchResult = async () =>{
            const data = await BookAPI.search(searchWord);
            return data;
        }
        SearchResult()
            .then(res => {setResult([...res]);})
                .catch(() => {
                    setResult("No results");
        });
    }

    if(Method ==='update'){
        const UpdataResult = async () =>{
            const update = await BookAPI.update(searchWord);
            return update;
        }
        UpdataResult()
            .then(update => {console.log()})
                .catch(() => {
                    console.log('%c Error occured when fetching books when update',
                    'background-color:black; font-size:20px;color:red;');
        });
    }
    
}
export default Output;
