/* eslint-disable eqeqeq */
/* eslint-disable jsx-a11y/anchor-is-valid */
/* eslint-disable no-unused-vars */

import { useEffect, useRef, useState } from "react";
import {Link} from "react-router-dom";
import Output from './SearchOutput'

const SearchPage = () => {
    const  [result, setResult] = useState([]);
    const inputElement = useRef();
    
    useEffect(()=>{
        inputElement.current.focus();
    },[])
    
    const handleSearch = (e) =>{
        if(e.target.value ===' ' || e.target.value[e.target.value.length-1] ===' '|| e.target.value.length === 0){
            setResult("");
            /* console.log('%c result clearedðŸ§¹ Postion One', 
                'background-color:black; color:white;font-size:30px;') */
        }else{
            Output(setResult, e.target.value)
            setResult("wait for results");
            /* console.log('%c result sent ðŸ“© Postion Two',
                'background-color:black; color:white;font-size:30px;') */
        }
        console.log(result, result.length)
    }
    
    
    return(
        <div className="search-books">
            <div className="search-books-bar">
                <Link to="/" className="close-search">Close</Link>
                <div className="search-books-input-wrapper">
                    <input
                    type="text"
                    placeholder="Search by title"
                    onChange={handleSearch}
                    ref={inputElement}
                    />
                </div>
            </div>

            <div className="search-books-results">
                    {
                        handleResult
                    }
                    
            </div>
        </div>
        )
}

