import { useEffect, useState } from "react";
import {Link} from "react-router-dom";
import MakeCurrentlyReading from "../Makeing Scripts/MakeCurrentlyReadingSection";
import MakeRead from "../Makeing Scripts/MakeReadSection";
import MakeWannaRead from "../Makeing Scripts/MakeWannaReadSection";
import Output from "./Output";

const BookShelf = ({handleMoveToShelf}) => {
  const [books , setBooks] = useState(['loading...']);
  
  const handleMoving = (e, book) =>{
    const Book = book;
    const shelf = e.target.value;
    const id =book.id;
    console.log(Book.title, shelf, id)
    handleMoveToShelf(Book, shelf, id)
  };


  useEffect(() => {
      Output('getall',setBooks)
  }, [])
  
  console.log(books)
  return (
    <div className='app'>
    <div className="list-books">
      <div className="list-books-title">
        <h1>MyReads</h1>
      </div>
      <div className="list-books-content">
                <div className="bookshelf">
                  <h2 className="bookshelf-title">Currently Reading</h2>
                  <div className="bookshelf-books">
                    <ol className="books-grid">
                      {
                        books[0] === 'loading...' ?
                        <h1>loading...</h1>
                        :
                          books.map((obj) => {
                            if(obj.shelf === 'currentlyReading'){
                              return(
                                <li key={obj.id}>
                                      <div className="book">
                                          <div className="book-top">
                                              <div
                                                  className="book-cover"
                                                  style={{
                                                  width: 128,
                                                  height: 193,
                                                  backgroundImage:
                                                  `url(${obj.imageLinks.thumbnail})`
                                                  }}
                                              ></div>
                                              <div className="book-shelf-changer">
                                                  <select onChange={handleMoving} id={obj.id}>
                                                      <option value="none" className="disabled-option">Move to... </option>
                                                      <option value="currentlyReading">Currently Reading</option>
                                                      <option value="wantToRead">Want to Read</option>
                                                      <option value="read">Read</option>
                                                  </select>
                                              </div>
                                          </div>
                                          <div className="book-title">{obj.title}</div>
                                          <div className="book-authors">{obj.authors}</div>
                                      </div>
                                  </li>
                              )
                            }
                          })
                      }
                    </ol>
                  </div>
                </div>
      </div>
      

    </div>
    <div className='open-search'>
      <Link to="/Search" >Add a book </Link>
    </div>
    </div>
  );
};

export default BookShelf;


