import "./App.css";
import {Route, Routes} from 'react-router-dom'
import BookShelf from "./Components/BookShelf";
import SearchPage from "./Components/SearchPage";
import {useState } from "react";

function App() {
  const [Shelf, setShelf] = useState([]);

  const handleMoveToShelf = (book, shelf, id) =>{
    
    if(shelf === 'none'){
      setShelf((prev) =>(
        prev.filter(obj => obj.id !== id)))
    }
    
    
  };

console.log(Shelf.length)

return (
    <Routes>
      <Route exact path="/" element={<BookShelf  Shelf={Shelf} handleMoveToShelf={handleMoveToShelf} />}/>
      <Route exact path="/Search" element={
      <SearchPage handleMoveToShelf={handleMoveToShelf} />}/>  
    </Routes>
  );
}

export default App;
