import { useEffect, useState } from "react";
import {Link} from "react-router-dom";

const BookShelf = ({updateValue}) => {
  const[currentReadShelf, setCurrentReadShelf] = useState([]);
  const[wannaToReadShelf, setWannaToReadShelf] = useState([]);
  const[readingShelf, setReadingShelf] = useState([]);

  const handleMoveToShelf = (book, shelf) =>{
    if(shelf ==='currentlyReading'){
      setCurrentReadShelf(...currentReadShelf)
    }
    
    if(shelf ==='wantToRead')
    {

    }
    
    if(shelf ==='read')
    {

    }
    
  };

  useEffect(() => {

  }, [updateValue])
  
  return (
    <div className='app'>
    <div className="list-books">
      <div className="list-books-title">
        <h1>MyReads</h1>
      </div>

      <div className="list-books-content">
        {/*Shelfes */}
        <div>
          {/* Currently Reading Shelf */}
          <div className="bookshelf">
            <h2 className="bookshelf-title">Currently Reading</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                <li>
                  <div className="book">
                    <div className="book-top">
                      <div
                        className="book-cover"
                        style={{
                          width: 128,
                          height: 193,
                          backgroundImage:
                            'url("http://books.google.com/books/content?id=PGR2AwAAQBAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE73-GnPVEyb7MOCxDzOYF1PTQRuf6nCss9LMNOSWBpxBrz8Pm2_mFtWMMg_Y1dx92HT7cUoQBeSWjs3oEztBVhUeDFQX6-tWlWz1-feexS0mlJPjotcwFqAg6hBYDXuK_bkyHD-y&source=gbs_api")',
                        }}
                      ></div>
                      <div className="book-shelf-changer">
                        <select>
                          <option value="none" disabled>
                            Move to...
                          </option>
                          <option value="currentlyReading">
                            Currently Reading
                          </option>
                          <option value="wantToRead">Want to Read</option>
                          <option value="read">Read</option>
                          <option value="none">None</option>
                        </select>
                      </div>
                    </div>
                    <div className="book-title">To Kill a Mockingbird</div>
                    <div className="book-authors">Harper Lee</div>
                  </div>
                </li>
                <li>
                  <div className="book">
                    <div className="book-top">
                      <div
                        className="book-cover"
                        style={{
                          width: 128,
                          height: 188,
                          backgroundImage:
                            'url("http://books.google.com/books/content?id=yDtCuFHXbAYC&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE72RRiTR6U5OUg3IY_LpHTL2NztVWAuZYNFE8dUuC0VlYabeyegLzpAnDPeWxE6RHi0C2ehrR9Gv20LH2dtjpbcUcs8YnH5VCCAH0Y2ICaKOTvrZTCObQbsfp4UbDqQyGISCZfGN&source=gbs_api")',
                        }}
                      ></div>
                      <div className="book-shelf-changer">
                        <select>
                          <option value="none" disabled>
                            Move to...
                          </option>
                          <option value="currentlyReading">
                            Currently Reading
                          </option>
                          <option value="wantToRead">Want to Read</option>
                          <option value="read">Read</option>
                          <option value="none">None</option>
                        </select>
                      </div>
                    </div>
                    <div className="book-title">Ender's Game</div>
                    <div className="book-authors">Orson Scott Card</div>
                  </div>
                </li>
              </ol>
            </div>
          </div>
          {/* Want to Read Shelf */}
          <div className="bookshelf">
            <h2 className="bookshelf-title">Want to Read</h2>
          </div>
          {/* Read Shelf */}
          <div className="bookshelf">
            <h2 className="bookshelf-title">Read</h2>
          </div>
        </div>
      </div>

    </div>
    <div className='open-search'>
      <Link to="/Search" >Add a book </Link>
    </div>
    </div>
  );
};

export default BookShelf;
