import "./App.css";
import {Route, Routes} from 'react-router-dom'
import BookShelf from "./Components/BookShelf";
import SearchPage from "./Components/SearchPage";
import {useState } from "react";
import Output from "./Components/SearchOutput";

function App() {
  const [Shelf, setShelf] = useState(['default']);

  const handleMoveToShelf = (book, shelf, id) =>{
    
    if(shelf === 'none'){
      setShelf((prev) =>(
        prev.filter(obj => obj.id !== id)))
    }

    if(shelf !== 'none'){
      if(Shelf.length === 'default'){
        setShelf({name:shelf, data:book, id:id})
        Shelf.length = Shelf.length +1
        console.log('from first')
      }

      if(Shelf.length !== 0) {
        setShelf((prev) =>(
          [...prev,{name:shelf, data:book, id:id}]))
        console.log('from sec')
      }
    }

    Output(book,shelf,'update')
  };



return (
    <Routes>
      <Route exact path="/" element={<BookShelf  Shelf={Shelf} handleMoveToShelf={handleMoveToShelf} />}/>
      <Route exact path="/Search" element={
      <SearchPage handleMoveToShelf={handleMoveToShelf} />}/>  
    </Routes>
  );
}

export default App;
