import {Link} from "react-router-dom";

const BookShelf = ({Shelf, handleMoveToShelf, result}) => {

  const handleMoving = (e) =>{
    const book = result.filter(obj => obj.id === e.target.id)
    const shelf = e.target.value;
    const id = e.target.id;
    handleMoveToShelf(book, shelf, id)
};



  return (
    <div className='app'>
    <div className="list-books">
      <div className="list-books-title">
        <h1>MyReads</h1>
      </div>

      <div className="list-books-content">
        {/*Shelfes */}
        <div>
          {/* Currently Reading Shelf */}
          <div className="bookshelf">
            <h2 className="bookshelf-title">Currently Reading</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                {Shelf.map((obj) => {
                    if(obj['name'] === 'currentlyReading'){
                      const book = obj['data']
                      return(
                        <li key={book[0].id}>
                            <div className="book">
                                <div className="book-top">
                                    <div
                                        className="book-cover"
                                        style={{
                                        width: 128,
                                        height: 193,
                                        backgroundImage:
                                            `url(${book[0].imageLinks.thumbnail})`
                                        }}
                                    ></div>
                                    <div className="book-shelf-changer">
                                        <select onChange={handleMoveToShelf} id={book[0].id}>
                                            <option value="none" className="disabled-option">Move to... </option>
                                            <option value="currentlyReading">Currently Reading</option>
                                            <option value="wantToRead">Want to Read</option>
                                            <option value="read">Read</option>
                                            <option value="none">None</option>
                                        </select>
                                    </div>
                                </div>
                                <div className="book-title disabled-option">{book[0].title}</div>
                                <div className="book-authors disabled-option">{book[0].authors}</div>
                            </div>
                        </li>
                      )
                    }
                  })}
              </ol>
            </div>
          </div>

          {/* Want to Read Shelf */}
          <div className="bookshelf">
            <h2 className="bookshelf-title">Want to Read</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                {Shelf.map((obj) => {
                    if(obj['name'] === 'wantToRead'){
                      const book = obj['data']
                      return(
                        <li key={book[0].id}>
                            <div className="book">
                                <div className="book-top">
                                    <div
                                        className="book-cover"
                                        style={{
                                        width: 128,
                                        height: 193,
                                        backgroundImage:
                                            `url(${book[0].imageLinks.thumbnail})`
                                        }}
                                    ></div>
                                    <div className="book-shelf-changer">
                                        <select onChange={handleMoveToShelf} id={book[0].id}>
                                            <option value="none" className="disabled-option">Move to... </option>
                                            <option value="currentlyReading">Currently Reading</option>
                                            <option value="wantToRead">Want to Read</option>
                                            <option value="read">Read</option>
                                            <option value="none">None</option>
                                        </select>
                                    </div>
                                </div>
                                <div className="book-title disabled-option">{book[0].title}</div>
                                <div className="book-authors disabled-option">{book[0].authors}</div>
                            </div>
                        </li>
                      )
                    }
                  })}
              </ol>
            </div>
          </div>

          {/* Read Shelf */}
          <div className="bookshelf">
            <h2 className="bookshelf-title">Read</h2>
            <div className="bookshelf-books">
              <ol className="books-grid">
                {Shelf.map((obj) => {
                    if(obj['name'] === 'read'){
                      const book = obj['data']
                      return(
                        <li key={book[0].id}>
                            <div className="book">
                                <div className="book-top">
                                    <div
                                        className="book-cover"
                                        style={{
                                        width: 128,
                                        height: 193,
                                        backgroundImage:
                                            `url(${book[0].imageLinks.thumbnail})`
                                        }}
                                    ></div>
                                    <div className="book-shelf-changer">
                                        <select onChange={handleMoveToShelf} id={book[0].id}>
                                            <option value="none" className="disabled-option">Move to... </option>
                                            <option value="currentlyReading">Currently Reading</option>
                                            <option value="wantToRead">Want to Read</option>
                                            <option value="read">Read</option>
                                            <option value="none">None</option>
                                        </select>
                                    </div>
                                </div>
                                <div className="book-title disabled-option">{book[0].title}</div>
                                <div className="book-authors disabled-option">{book[0].authors}</div>
                            </div>
                        </li>
                      )
                    }
                  })}
              </ol>
            </div>
          </div>

        </div>
        
      </div>

    </div>
    <div className='open-search'>
      <Link to="/Search" >Add a book </Link>
    </div>
    </div>
  );
};

export default BookShelf;
