import "./App.css";
import {Route, Routes} from 'react-router-dom'
import BookShelf from "./Components/BookShelf";
import SearchPage from "./Components/SearchPage";
import {useEffect, useState } from "react";
import Output from "./Components/Output";

function App() {
  const [Shelf, setShelf] = useState(['']);
  const [updatestate, setUpfateState] = useState(true);
  const [update, setUpdate] = useState(false);

  const handleMoveToShelf = (book, shelf) =>{

    if(book.shelf){
      setShelf((prev) =>(prev.filter(obj => obj.id !== book.id)))
        Output('update', book,shelf)
        console.log(Shelf)
        
        /* setInterval(() => {
          window.location.reload();
        }, 270); */
    }

    if(shelf === 'none' && !(book.shelf)){
      setShelf((prev) =>(
        prev.filter(obj => obj.id !== book.id)))
        console.log('removed')
        setUpdate(!update)
    }

    if(shelf !== 'none' && !(book.shelf)){
      Output('update', book[0],shelf)
      console.log('add')
      setUpdate(!update)
      
    }
    setUpfateState(!updatestate)
  };

  useEffect(() => {
    
    Output('getall',setShelf)
    setUpdate(!update)
}, [updatestate])

  


return (
    <Routes>
      <Route exact path="/" element={<BookShelf  
      Shelf={Shelf} handleMoveToShelf={handleMoveToShelf}
      update={update}  setUpdate={setUpdate} />}/>
      
      <Route exact path="/Search" element={
      <SearchPage handleMoveToShelf={handleMoveToShelf} />}/>  
    </Routes>
  );
}

export default App;
