/* eslint-disable react-hooks/exhaustive-deps */
import { useEffect, useState } from "react";



const HandleResult = ({result,  handleMoveToShelf}) =>{
    const [returnvalue, setReturnvalue] =useState('Type in search box');

    const handleMoving = (e) =>{
        const book = result.filter(obj => obj.id === e.target.id)
        const shelf = e.target.value;
        const id = e.target.id;
        cons
        handleMoveToShelf(book, shelf, id)
    };

    useEffect(() =>{
        if(result === [] || result === null){
            setReturnvalue('Type in search box');
        }
        if(result ==="wait for results"){
            setReturnvalue('Searching...');
        }
        if(result ==="No results"){
                    setReturnvalue("No results");
                }
        if(typeof result === 'object'){
            setReturnvalue(
                    <ol className="books-grid">
                        {result.map(obj =>{
                            return(
                                <li key={obj.id}>
                                    <div className="book">
                                        <div className="book-top">
                                            <div
                                                className="book-cover"
                                                style={{
                                                width: 128,
                                                height: 193,
                                                backgroundImage:
                                                    `url(${obj.imageLinks.thumbnail})`
                                                }}
                                            ></div>
                                            <div className="book-shelf-changer">
                                                <select onChange={handleMoving} id={obj.id}>
                                                    <option value="none" className="disabled-option">Move to... </option>
                                                    <option value="currentlyReading">Currently Reading</option>
                                                    <option value="wantToRead">Want to Read</option>
                                                    <option value="read">Read</option>
                                                    <option value="none">None</option>
                                                </select>
                                            </div>
                                        </div>
                                        <div className="book-title">{obj.title}</div>
                                        <div className="book-authors">{obj.authors[0]}</div>
                                    </div>
                                </li>)})}
                    </ol>
                );
        }

        
    },[result])
    return (returnvalue);
}

export default HandleResult;
