import { useState } from "react";



const HandleResult = ({result}) =>{
    const [returnvalue, setReturnvalue] =useState((<h1>Type in search box</h1>));

    if(result.length===0){
        setReturnvalue(<h1>Type in search box</h1>);
    }

    if(result ==="wait for results"){
        setReturnvalue(<h1>Searching...</h1>);
    }

    else{
        setReturnvalue(
                <ol className="books-grid">
                    {result.map(obj =>{
                        return(
                            <li key={obj.id}>
                                <div className="book">
                                    <div className="book-top">
                                        <div
                                            className="book-cover"
                                            style={{
                                            width: 128,
                                            height: 193,
                                            backgroundImage:
                                                `url(${obj.imageLinks.thumbnail})`
                                            }}
                                        ></div>
                
                                        <div className="book-shelf-changer">
                                            <select>
                                                <option value="none" disabled>
                                                    Move to...
                                                </option>
                                                <option value="currentlyReading">
                                                    Currently Reading
                                                </option>
                                                <option value="wantToRead">Want to Read</option>
                                                <option value="read">Read</option>
                                                <option value="none">None</option>
                                            </select>
                                        </div>
                                    </div>
                                    <div className="book-title">{obj.title}</div>
                                    <div className="book-authors">{obj.authors[0]}</div>
                                </div>
                            </li>)})}
                </ol>
            );
    }

    switch(result){
        case result.length===0:
            setReturnvalue(<h1>Type in search box</h1>);
            break;

        case result ==="wait for results":
            setReturnvalue(<h1>Searching...</h1>);
            break;
        case typeof result === 'object':
            setReturnvalue(
                <ol className="books-grid">
                    {result.map(obj =>{
                        return(
                            <li key={obj.id}>
                                <div className="book">
                                    <div className="book-top">
                                        <div
                                            className="book-cover"
                                            style={{
                                            width: 128,
                                            height: 193,
                                            backgroundImage:
                                                `url(${obj.imageLinks.thumbnail})`
                                            }}
                                        ></div>
                
                                        <div className="book-shelf-changer">
                                            <select>
                                                <option value="none" disabled>
                                                    Move to...
                                                </option>
                                                <option value="currentlyReading">
                                                    Currently Reading
                                                </option>
                                                <option value="wantToRead">Want to Read</option>
                                                <option value="read">Read</option>
                                                <option value="none">None</option>
                                            </select>
                                        </div>
                                    </div>
                                    <div className="book-title">{obj.title}</div>
                                    <div className="book-authors">{obj.authors[0]}</div>
                                </div>
                            </li>)})}
                </ol>
            );
            break;
        default:
    }
    console.log(returnvalue)
    return returnvalue;
}

export default HandleResult;
